import Comments from "./Comments";

const Post = ({ title, body, comments, id }) => {
  return (
    <div style={{ textAlign: "left", marginBottom: "12px" }}>
      <h3>{title}</h3>
      <p>{body}</p>
      {<Comments postId={id} initialComments={comments} />}
    </div>
  );
};

export default Post;
